function setStory(){
  storyCountHr = storyCount+1;//set a human friendly storyCount

  $("#storyTitle").html("<h1>Story "+storyCountHr+" of " + storyList.length + "</h1><p class='text-center'>Mouse over elements for more detail</p>")
  $("#story").html("<blockquote>"+storyList[storyCount]+"</blockquote><br>");
  $('[data-toggle="tooltip"]').tooltip();
}

function nextStory(){
  maxCount = storyList.length;
  storyCount++;

  if(storyCount == maxCount){
    storyCount = 0;
    setStory();
    $('#evaluationModal').modal('toggle');
  }
  else{
    setStory();
  }
}


